{"metadata":{"kernelspec":{"language":"python","display_name":"Python 3","name":"python3"},"language_info":{"name":"python","version":"3.11.13","mimetype":"text/x-python","codemirror_mode":{"name":"ipython","version":3},"pygments_lexer":"ipython3","nbconvert_exporter":"python","file_extension":".py"},"kaggle":{"accelerator":"none","dataSources":[{"sourceId":14000273,"sourceType":"datasetVersion","datasetId":8921105},{"sourceId":283945761,"sourceType":"kernelVersion"}],"dockerImageVersionId":31192,"isInternetEnabled":true,"language":"python","sourceType":"script","isGpuEnabled":false}},"nbformat_minor":4,"nbformat":4,"cells":[{"cell_type":"code","source":"# %% [code] {\"execution\":{\"iopub.status.busy\":\"2025-12-04T23:59:14.246296Z\",\"iopub.execute_input\":\"2025-12-04T23:59:14.246628Z\",\"iopub.status.idle\":\"2025-12-04T23:59:19.162326Z\",\"shell.execute_reply.started\":\"2025-12-04T23:59:14.246601Z\",\"shell.execute_reply\":\"2025-12-04T23:59:19.161288Z\"}}\nimport warnings\nwarnings.filterwarnings('ignore')\n!pip install streamlit\nimport streamlit as st\nimport pandas as pd\nimport numpy as np\nimport joblib\n\n# %% [code] {\"execution\":{\"iopub.status.busy\":\"2025-12-04T23:59:19.163796Z\",\"iopub.execute_input\":\"2025-12-04T23:59:19.164301Z\",\"iopub.status.idle\":\"2025-12-04T23:59:22.653494Z\",\"shell.execute_reply.started\":\"2025-12-04T23:59:19.164275Z\",\"shell.execute_reply\":\"2025-12-04T23:59:22.650143Z\"}}\nmodel = joblib.load(\"/kaggle/input/accident-models/lgb_model.pkl\")\nthresholds = joblib.load(\"/kaggle/input/accident-models/thresholds (2).pkl\")\nle = joblib.load(\"/kaggle/input/accident-models/label_encoder.pkl\")\nfreq_maps = joblib.load(\"/kaggle/input/accident-models/freq_maps.pkl\")\nscaler = joblib.load(\"/kaggle/input/accident-models/scaler (1).pkl\")\n\n# Streamlit title\nst.title(\"Accident Severity Prediction\")\n\n# Example inputs\nspeed_limit = st.number_input(\"Speed Limit\", min_value=0, max_value=100, value=30)\nurban_rural = st.selectbox(\"Urban or Rural Area\", [1, 2, 3])\nlight_condition = st.selectbox(\"Light Conditions\", [\n    'Daylight: Street light present',\n    'Darkness: Street lights present and lit',\n    'Darkness: Street lighting unknown',\n    'Darkness: Street lights present but unlit',\n    'Darkeness: No street lighting'\n])\nroad_surface = st.selectbox(\"Road Surface Conditions\", ['Dry','Wet/Damp','Frost/Ice','Snow','Flood (Over 3cm of water)'])\n\n# Prepare input\ninput_df = pd.DataFrame({\n    \"Speed_limit\": [speed_limit],\n    \"Urban_or_Rural_Area\": [urban_rural],\n    \"Light_Conditions\": [light_condition],\n    \"Road_Surface_Conditions\": [road_surface],\n})\n\n# Mapping categorical features\nlight_mapping = {\n    'Daylight: Street light present': 4,\n    'Darkness: Street lights present and lit': 3,\n    'Darkness: Street lighting unknown': 2,\n    'Darkness: Street lights present but unlit': 1,\n    'Darkeness: No street lighting': 0, \n}\nsurface_mapping = {\n    'Dry': 4, 'Wet/Damp': 3, 'Frost/Ice': 2, 'Snow': 1, 'Flood (Over 3cm of water)': 0,\n}\n\ninput_df['Light_Conditions'] = input_df['Light_Conditions'].map(light_mapping).fillna(-1)\ninput_df['Road_Surface_Conditions'] = input_df['Road_Surface_Conditions'].map(surface_mapping).fillna(-1)\n\n# Feature engineering\ninput_df['Speed_Urban_Rural'] = input_df['Urban_or_Rural_Area'] * input_df['Speed_limit']\ninput_df['Light_Road_Interaction'] = input_df['Light_Conditions'] * input_df['Road_Surface_Conditions']\n\n# Add missing columns with default 0\nfor col in model.feature_name_:\n    if col not in input_df.columns:\n        input_df[col] = 0\n\n# Apply frequency mapping only to existing columns\nfor col in freq_maps:\n    if col in input_df.columns:\n        input_df[col] = input_df[col].map(freq_maps[col]).fillna(0)\n\n# Prediction\nprobs = model.predict_proba(input_df)\npred = np.zeros(len(probs), dtype=int)\nfor i, th in enumerate(thresholds):\n    mask = probs[:, i] >= th\n    pred[mask] = i\n\n# Handle rows where no class met threshold\nmask_no_class = ~np.any(probs >= thresholds, axis=1)\npred[mask_no_class] = np.argmax(probs[mask_no_class], axis=1)\n\npred_labels = le.inverse_transform(pred)\n\nst.write(\"Predicted Accident Severity:\", pred_labels[0])\n\n\n# %% [code] {\"execution\":{\"iopub.status.busy\":\"2025-12-05T00:06:19.214914Z\",\"iopub.execute_input\":\"2025-12-05T00:06:19.215333Z\",\"iopub.status.idle\":\"2025-12-05T00:06:19.222619Z\",\"shell.execute_reply.started\":\"2025-12-05T00:06:19.215305Z\",\"shell.execute_reply\":\"2025-12-05T00:06:19.221593Z\"}}\n%%writefile requirements.txt\nstreamlit\nnumpy\npandas\nscikit-learn\nlightgbm\njoblib\n\n\n# %% [code]\n","metadata":{"_uuid":"f83d3bda-6762-4592-a3a1-4c3617f8fecd","_cell_guid":"ae7e2927-e748-457b-a8ab-0321fecd78f6","trusted":true,"collapsed":false,"jupyter":{"outputs_hidden":false}},"outputs":[],"execution_count":null}]}