{"metadata":{"kernelspec":{"language":"python","display_name":"Python 3","name":"python3"},"language_info":{"name":"python","version":"3.11.13","mimetype":"text/x-python","codemirror_mode":{"name":"ipython","version":3},"pygments_lexer":"ipython3","nbconvert_exporter":"python","file_extension":".py"},"kaggle":{"accelerator":"none","dataSources":[{"sourceId":14000635,"sourceType":"datasetVersion","datasetId":8921307},{"sourceId":283945761,"sourceType":"kernelVersion"}],"dockerImageVersionId":31192,"isInternetEnabled":true,"language":"python","sourceType":"script","isGpuEnabled":false}},"nbformat_minor":4,"nbformat":4,"cells":[{"cell_type":"code","source":"# %% [code] {\"execution\":{\"iopub.status.busy\":\"2025-12-05T12:13:05.635835Z\",\"iopub.execute_input\":\"2025-12-05T12:13:05.636362Z\",\"iopub.status.idle\":\"2025-12-05T12:13:16.584868Z\",\"shell.execute_reply.started\":\"2025-12-05T12:13:05.636330Z\",\"shell.execute_reply\":\"2025-12-05T12:13:16.583525Z\"}}\n!pip install streamlit\n\n\n# %% [code] {\"jupyter\":{\"outputs_hidden\":false},\"execution\":{\"iopub.status.busy\":\"2025-12-05T12:19:25.467034Z\",\"iopub.execute_input\":\"2025-12-05T12:19:25.467441Z\",\"iopub.status.idle\":\"2025-12-05T12:19:32.648664Z\",\"shell.execute_reply.started\":\"2025-12-05T12:19:25.467413Z\",\"shell.execute_reply\":\"2025-12-05T12:19:32.645381Z\"}}\nimport warnings\nwarnings.filterwarnings('ignore')\n\nimport streamlit as st\nimport pandas as pd\nimport numpy as np\nimport joblib\nimport os\n\n\n# --- Auto-detect environment ---\ndef is_kaggle_environment():\n    return os.path.exists(\"/kaggle/input\")\n\nif is_kaggle_environment():\n    BASE_PATH = \"/kaggle/input/accident-prediction-model/\"\nelse:\n    # For Streamlit Cloud, files must be inside a folder named \"accident-prediction-model\"\n    BASE_PATH = \"accident-prediction-model/\"\n\n@st.cache_resource\ndef load_model_and_resources():\n    try:\n        model = joblib.load(BASE_PATH + \"lgb_model.pkl\")\n        thresholds = joblib.load(BASE_PATH + \"thresholds.pkl\")\n        scaler = joblib.load(BASE_PATH + \"scaler.pkl\")\n        le = joblib.load(BASE_PATH + \"label_encoder.pkl\")\n        freq_maps = joblib.load(BASE_PATH + \"freq_maps.pkl\")\n        return model, thresholds, scaler, le, freq_maps\n\n    except FileNotFoundError as e:\n        st.error(f\"Missing File: {e.filename}\")\n        st.error(f\"Looking in: {BASE_PATH}\")\n        st.stop()\n\n    except Exception as e:\n        st.error(f\"Unexpected error: {e}\")\n        st.stop()\n\n\nmodel, thresholds, scaler, le, freq_maps = load_model_and_resources()\n\nst.title(\"Accident Severity Prediction\")\n\nspeed_limit = st.number_input(\"Speed Limit\", min_value=0, max_value=100, value=30)\nurban_rural = st.selectbox(\"Urban or Rural Area\", [1, 2, 3])\nlight_condition = st.selectbox(\"Light Conditions\", [\n    'Daylight: Street light present',\n    'Darkness: Street lights present and lit',\n    'Darkness: Street lighting unknown',\n    'Darkness: Street lights present but unlit',\n    'Darkness: No street lighting'\n])\nroad_surface = st.selectbox(\"Road Surface Conditions\", [\n    'Dry', 'Wet/Damp', 'Frost/Ice', 'Snow', 'Flood (Over 3cm of water)'\n])\n\ninput_df = pd.DataFrame({\n    \"Speed_limit\": [speed_limit],\n    \"Urban_or_Rural_Area\": [urban_rural],\n    \"Light_Conditions\": [light_condition],\n    \"Road_Surface_Conditions\": [road_surface],\n})\n\nlight_mapping = {\n    'Daylight: Street light present': 4,\n    'Darkness: Street lights present and lit': 3,\n    'Darkness: Street lighting unknown': 2,\n    'Darkness: Street lights present but unlit': 1,\n    'Darkness: No street lighting': 0, \n}\nsurface_mapping = {\n    'Dry': 4, 'Wet/Damp': 3, 'Frost/Ice': 2, 'Snow': 1, 'Flood (Over 3cm of water)': 0,\n}\n\ninput_df['Light_Conditions'] = input_df['Light_Conditions'].map(light_mapping).fillna(-1)\ninput_df['Road_Surface_Conditions'] = input_df['Road_Surface_Conditions'].map(surface_mapping).fillna(-1)\n\ninput_df['Speed_Urban_Rural'] = input_df['Urban_or_Rural_Area'] * input_df['Speed_limit']\ninput_df['Light_Road_Interaction'] = input_df['Light_Conditions'] * input_df['Road_Surface_Conditions']\n\nfor col in model.feature_name_:\n    if col not in input_df.columns:\n        input_df[col] = 0\n\nfor col in freq_maps:\n    if col in input_df.columns:\n        input_df[col] = input_df[col].map(freq_maps[col]).fillna(0)\n\nprobs = model.predict_proba(input_df)\npred = np.zeros(len(probs), dtype=int)\nfor i, th in enumerate(thresholds):\n    mask = probs[:, i] >= th\n    pred[mask] = i\n\nmask_no_class = ~np.any(probs >= thresholds, axis=1)\npred[mask_no_class] = np.argmax(probs[mask_no_class], axis=1)\n\npred_labels = le.inverse_transform(pred)\nst.write(\"Predicted Accident Severity:\", pred_labels[0])\n\n# %% [code]\n","metadata":{"_uuid":"3fd81299-a992-4d31-bf21-bbaab243fe91","_cell_guid":"3d4061ea-19c8-420b-b8e1-e2b593d64a0d","trusted":true,"collapsed":false,"jupyter":{"outputs_hidden":false}},"outputs":[],"execution_count":null}]}